func stmtCSVHref(accountCode, from, to string) templ.SafeURL {
	url := "/reports/statement?account=" + accountCode + "&format=csv"
	if from != "" {
		url += "&from=" + from
	}
	if to != "" {
		url += "&to=" + to
	}
	return templ.SafeURL(url)
}

