func plProductOnHand(stock *app.StockResult, code string) decimal.Decimal {
	if stock == nil {
		return decimal.Zero
	}
	for _, s := range stock.Levels {
		if s.ProductCode == code {
			return s.OnHand
		}
	}
	return decimal.Zero
}

